COMMENT ON TABLE TSTATUS_DICT
IS
    '状态机字典表';
COMMENT ON COLUMN TSTATUS_DICT.CUR_STATUS
IS
    '当前状态';
COMMENT ON COLUMN TSTATUS_DICT.CUR_STATUS_DESC
IS
    '当前状态描述';
COMMENT ON COLUMN TSTATUS_DICT.PREVIOUS_STATUS
IS
    '上一步状态';
COMMENT ON COLUMN TSTATUS_DICT.CAN_BACK
IS
    '能否回退到上一步状态 0：否，1：是  2:回退两步';
